it('compareToTests[0]', () => {
  expect(UInt8.from(124).compareTo(UInt64.from(124))).toBe(0)
})

it('compareToTest[1]', () => {
  expect(UInt64.from(124).compareTo(UInt8.from(124))).toBe(0)
})

it('compareToTest[2]', () => {
  expect(UInt64.from(124).compareTo(UInt8.from(123))).toBe(1)
})

